import typeToReducer from "type-to-reducer";
import {
  SAVE_TODO,
} from "../common/types";

const initialState = {
 todoList: [],
};

export const user = typeToReducer(
  {
    [SAVE_TODO]: (state: any, action: any) => {
     const addTodo = state.todoList.push(action.payload)

    },
},
initialState
)

export default user;
