import typeToReducer from "type-to-reducer";
import {
  SAVE_TODO,
} from "../common/types";

const initialState = {
 todoList: [],
};

export const user = typeToReducer(
  {
    [SAVE_TODO]: (state: any, action: any) => {
      state.todoList.push(action.payload)
      return
    },
},
initialState
)

export default user;
