import React, { useState } from "react";

interface IProps {
  todoList: Array<{
    id: number;
    task: string;
    checked: boolean;


  }>;
  saveTodo: (text: string) => void;
  deleteTodo: (id: number) => void;
  isDone: (id: number, checked:boolean) => void;
}

const List = ({ todoList, saveTodo, deleteTodo, isDone }: IProps) => {
  const [text, setText] = useState("");

  const addTodo = () => {
    console.log(`adding ${text}`);
    saveTodo(text);
    setText("");
  };

  return (
    <div>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button disabled={text.length === 0} onClick={addTodo}>
        Add
      </button>
      {todoList.map((item) => (
        <div>
          <input>{item.task}</input>
          <div onClick={() => deleteTodo(item.id)}>X</div>
          <input type="checkbox" onChange={(e) => isDone(item.id, e.target.checked)}/>
        </div>
      ))}
    </div>
  );
};

export default List;
