import typeToReducer from "type-to-reducer";
import { SAVE_TODO, DELETE_TODO, IS_DONE,  } from "../common/types";

interface stateTodo {
  todoList: Array<{
    id: number;
    task: string;
    checked: boolean;
    edit: boolean;
}>
}

const initialState: stateTodo = {
  todoList: [],
};

export const user = typeToReducer(
  {
    [SAVE_TODO]: (state: stateTodo, action: any) => ({
      ...state,
      todoList: [
        ...state.todoList,
        {
          id: state.todoList.length,
          task: action.payload.text,
          checked: false,
        },
      ],
    }),
    [DELETE_TODO]: (state: stateTodo, action: any) => ({
      ...state,
      todoList: state.todoList.filter(item => action.payload.id !== item.id)
    }),
    [IS_DONE]: (state: stateTodo, action: any) => ({
      ...state, 
      todoList: state.todoList.map(item => item.id === action.payload.id ? ({
        ...item,
       checked: action.payload.checked 
      }): item)
    })
  },

  initialState
);

export default user;
