import React, { useState } from "react";

interface IProps {
  todoList: Array<{
    id: number;
    text: string;
    checked: boolean;
  }>;
  saveTodo: (text: string) => void;
  deleteTodo: (id: number) => void;
}

const List = ({ todoList, saveTodo, deleteTodo }: IProps) => {
  const [text, setText] = useState("");

  const addTodo = () => {
    console.log(`adding ${text}`);
    saveTodo(text);
    setText("");
  };

  return (
    <div>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button disabled={text.length === 0} onClick={addTodo}>
        Add
      </button>
      {todoList.map((item) => (
        <div>
          <div>{item.text}</div>
          <div onClick={() => deleteTodo(item.id)}>X</div>
        </div>
      ))}
    </div>
  );
};

export default List;
