import typeToReducer from "type-to-reducer";
import {
  SAVE_TODO,
} from "../common/types";

const initialState = {
 todo: []
};

export const user = typeToReducer(
  {
    [SAVE_TODO]: (state: any, action: any) => ({
      ...state,
      list: action.payload
    }),
},initialState)

export default user;
