import { SAVE_TODO } from "../common/types";

export const saveTodo = (text: string) => (dispatch: any) => {
  try {
    dispatch({
      type: SAVE_TODO,
      payload: { text },
    });
  } catch (err) {
    console.log(`err`, err);
  }
};

export const deleteTodo = (id: number) => (dispatch: any) => {
  try {
    dispatch({
      type: SAVE_TODO,
      payload: { text },
    });
  } catch (err) {
    console.log(`err`, err);
  }
};
