import React, { useState } from "react";
import "./List.scss";
import "bootstrap-icons/font/bootstrap-icons.css"

interface IProps {
  todoList: Array<{
    id: number;
    task: string;
    checked: boolean;
    isEdit: boolean;
  }>;
  saveTodo: (task: string) => void;
  deleteTodo: (id: number) => void;
  isDone: (id: number, checked: boolean) => void;
  toggleEdit: (id: number, isEdit: boolean) => void;
  changeTodo: (id: number, task: string) => void;
}

const List = ({
  todoList,
  saveTodo,
  deleteTodo,
  isDone,
  toggleEdit,
  changeTodo,
}: IProps) => {
  const [text, setText] = useState("");

  const addTodo = () => {
    console.log(`adding ${text}`);
    saveTodo(text);
    setText("");
  };

  return (
    <div className="list-wrapper">
      <h1>TODO LIST</h1>
      <div className="list-box">
        <div className="add-section">
          <input
            placeholder="ADD YOUR TASK"
            className="add-input"
            type="text"
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
          <button disabled={text.length === 0} onClick={addTodo}>
            Add
          </button>
        </div>

        <div className="task-container">
          {todoList.map((item) => (
            <div className="task-item" key={item.id}>
              <span className="task">
                {item.isEdit ? (
                  <input
                    type="text"
                    value={item.task}
                    onChange={(e) => changeTodo(item.id, e.target.value)}
                  />
                ) : (
                  item.task
                )}
              </span>
              <div onClick={() => toggleEdit(item.id, item.isEdit)}>edit</div>
              <div onClick={() => deleteTodo(item.id)}>x</div>
              <input
                type="checkbox"
                onChange={(e) => isDone(item.id, e.target.checked)}
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default List;
