import typeToReducer from "type-to-reducer";
import {
  USER_LOADED,
} from "";

const initialState = {
  token: localStorage.getItem("token"),
  isAuthenticated: false,
  loading: false,
  user: null,
};

export const user = typeToReducer(
  {
    [USER_LOADED]: (state: any, action: any) => ({
      ...state,
    }),
})

export default user;
