import { SAVE_TODO } from "../common/types";

export const saveTodo = (text: string) => async (dispatch: any) => {
  try {
    dispatch({
      type: SAVE_TODO,
      payload: [
        {
          id: Date.now(),
          name: {text},
          done: false,
        },
      ],
    });
  } catch (err) {
    console.log(`err`, err);
  }
};
