import typeToReducer from "type-to-reducer";
import {
    LIST_LOADED,
} from "../common/types";

const initialState = {
 list: []
};

export const user = typeToReducer(
  {
    [LIST_LOADED]: (state: any, action: any) => ({
      ...state,
      list: action.payload
    }),
},initialState)

export default user;
