import { SAVE_TODO } from "../common/types";

export const loadList = ({input: string}) => async (dispatch: any) => {
  try {
    dispatch({
      type: SAVE_TODO,
      payload: [
        {
          id: Date.now(),
          name: input,
          done: false,
        },
      ],
    });
  } catch (err) {
    console.log(`err`, err);
  }
};
