import { SAVE_TODO, DELETE_TODO, IS_DONE } from "../common/types";

export const saveTodo = (text: string) => (dispatch: any) => {
  try {
    dispatch({
      type: SAVE_TODO,
      payload: { text },
    });
  } catch (err) {
    console.log(`err`, err);
  }
};

export const deleteTodo = (id: number) => (dispatch: any) => {
  try {
    dispatch({
      type: DELETE_TODO,
      payload: { id },
    });
  } catch (err) {
    console.log(`err`, err);
  }
};

export const isDone = (id: number, checked) => (dispatch: any) => {
  try {
    dispatch({
      type: DELETE_TODO,
      payload: { id },
    });
  } catch (err) {
    console.log(`err`, err);
  }
};
